import React, { Component } from 'react';
import Axios from 'axios';
import { LineChart, Line } from 'recharts';
import './App.css';

class App extends Component {
  state={
    chartdata:[
      {name: 'Page A', uv: 4000, pv: 2400, amt: 2400},
      {name: 'Page B', uv: 3000, pv: 1398, amt: 2210},
      {name: 'Page C', uv: 2000, pv: 9800, amt: 2290},
      {name: 'Page D', uv: 2780, pv: 3908, amt: 2000},
      {name: 'Page E', uv: 1890, pv: 4800, amt: 2181},
      {name: 'Page F', uv: 2390, pv: 3800, amt: 2500},
      {name: 'Page G', uv: 3490, pv: 4300, amt: 2100},
    ],
    countries:[]
  }
  componentDidMount(){
    this.getData();
  }
  async getData(){
    const res=await Axios.get("https://pomber.github.io/covid19/timeseries.json");

    this.setState({
      countries:res.data.China
    })
    console.log(this.state.total);
  }
  render(){
    return (
      <div className="App">
        <h1>Hi</h1>
        {
          this.state.countries.map((list,i)=>
        <li key={i}>{list.date} deaths: {list.deaths}</li>
          )
        }
        <div className="chart">
          <p>I am here</p>
          <LineChart width={400} height={400} data={this.state.chartdata}>
            <Line type="monotone" datakey="uv" stroke="#8884d8"></Line>
          </LineChart>
        </div>
      </div>

      )
  }
}

export default App;
