import React, { Component } from 'react';
import {Container,Row,Col,Table,Form, Card} from 'react-bootstrap';

class LandingPage extends Component{
    state={
        total:0,
        incidents:[]
      }

      componentDidMount(){
        this.getData();
      }
      async getData(){
      const res=await Axios.get("https://covid19.mathdro.id/api/deaths");
      const totalDeaths=await Axios.get("https://covid19.mathdro.id/api");
      this.setState({
        incidents:res.data.filter(row=> row.lat !== null),
        total:totalDeaths.data.deaths.value
      })

      }
    render(){
        return(
            <div className="mid">
                <Container fluid>
                    <Row className="justify-content-md-center">
                        <Col xs="12" lg="8">
                        <Card className="shadow-sm" style={{marginBottom:'1rem',padding:'1rem',border:'none'}}>
                            <Map 
                            style={{height:"300px",width:"100%"}}
                            zoom={1}
                            center={[-0.09,51.505]}
                            >
                            <TileLayer url="http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

                            {
                            this.state.incidents.map((city,i) => {
                                return (
                                <CircleMarker 
                                key={i} 
                                center={[city.lat, city.long]} 
                                readius={20 * Math.log(city.deaths / this.state.total)}
                                fillOpacity={0.5}
                                fillColor={"red"}
                                stroke={false}
                                />
                                )
                            })
                            }
                            </Map>
                        </Card>     
                        </Col>
                    </Row>
                </Container>
            </div>
        )
    }
}
export default LandingPage;
