import React, { Component } from 'react';
import Axios from 'axios';
import { LineChart, Line, XAxis,YAxis, CartesianGrid,Tooltip,Legend} from 'recharts';
import './App.css';
import Header from './components/header';

class App extends Component {
  constructor (props){
    super(props);
    this.getCountry=this.getCountry.bind(this);
  }
  state={
    temp:[],
    countries:[]
  }
  componentDidMount(){
    this.getData();
  }
  async getData(){
    const res=await Axios.get("https://pomber.github.io/covid19/timeseries.json");

    this.setState({
      countries:res.data.Iran,
      temp:Object.keys(res.data)
    })
    console.log(this.state.temp);
  }
  async getCountry(event){
    event.persist();
    const countryRes=await Axios.get("https://pomber.github.io/covid19/timeseries.json");
    const searchCountry=event.target.value;
    console.log(countryRes.data[searchCountry]);

    this.setState({
      countries:countryRes.data[searchCountry]
    })
  }
  render(){
    return (
      <div className="App">
        <Header />
        {
          this.state.countries.map((list,i)=>
        <li key={i}>{list.date} deaths: {list.deaths}</li>
          )
        }
        <div className="options">
          <select className="countrySelect" onChange={this.getCountry}>
            {
              this.state.temp.map((items,i)=>
              <option key={i}>{items}</option>  
              )
            }
          </select>
        </div>
        <div className="chart" style={{width:"100%"}}>
          <p>I am here</p>
            <LineChart width={1200} height={600} data={this.state.countries}>
            <CartesianGrid strokeDasharray="1 1" />
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="confirmed" dot={false} points={1} stroke="#1B262C" strokeWidth={2} />
            <Line type="monotone" dataKey="recovered" dot={false} points={1} stroke="#323Edd" strokeWidth={2} />
            <Line type="monotone" dataKey="deaths" dot={false} points={1} stroke="#C81912" strokeWidth={2} />
            </LineChart>

        </div>
      </div>

      )
  }
}

export default App;
