import React, { Component } from 'react';
import Axios from 'axios';
import './App.css';

class App extends Component {
  state={
    countries:[]
  }
  componentDidMount(){
    this.getData();
  }
  async getData(){
    const res=await Axios.get("https://pomber.github.io/covid19/timeseries.json");
    this.setState({
      countries:res.data
    })
    console.log(this.state.countries.Iran);
  }
  render(){
    return (
      this.state.countries.map((data,i)=>
      <li key={i}>{data.date}</li>
      )
      )
  }
}

export default App;
