import React, { Component } from 'react';
import Axios from 'axios';
import { Map, CircleMarker,TileLayer} from 'react-leaflet';
import {Container,Row,Col, Card, Alert} from 'react-bootstrap';
import NumberFormat from 'react-number-format';
import { BarChart, Bar, XAxis,YAxis, CartesianGrid,Tooltip,Legend} from 'recharts';
import "leaflet/dist/leaflet.css";

const colors = {
    confirmed: 'hsla(27, 100%, 51%, 1)',
    recovered: 'hsla(91, 100%, 51%, 1)',
    deaths: 'hsla(0, 100%, 51%, 1)',
  };
class LandingPage extends Component{
    state={
        confirmed:0,
        recovered:0,
        deaths:0,
        total:0,
        incidents:[],
        incidentsSorted:[],
        width:
        window.innerWidth ||
        document.documentElement.clientWidth ||
        document.body.clientWidth
      }

      componentDidMount(){
        this.getData();
        window.addEventListener('resize',this.updateDimensions);
      }
      updateDimensions = ()=>{
        const width = 
        window.innerWidth ||
        document.documentElement.clientWidth ||
        document.body.clientWidth;

        this.setState({width});
    }
      async getData(){
      const res=await Axios.get("https://covid19.mathdro.id/api/confirmed");
      const totalDeaths=await Axios.get("https://covid19.mathdro.id/api");
      const filteredData=res.data.filter(row=> row.lat !== null)
      const sortfilteredData=res.data.filter(row=>row.countryRegion !== "US")
      const incidentsSorted=sortfilteredData.sort(function(a,b){
        return b.confirmed - a.confirmed;
    });
    console.log(incidentsSorted);
      this.setState({
        incidents:filteredData,
        incidentsSorted,
        total:totalDeaths.data.deaths.value,
        confirmed:totalDeaths.data.confirmed.value,
        recovered:totalDeaths.data.recovered.value,
        deaths:totalDeaths.data.deaths.value
      })
      console.log(this.state.incidents);
      }
    render(){
        const {
            confirmed,
            recovered,
            deaths,
            total,
            incidents,
            incidentsSorted,
            width
        } = this.state;
        return(
            <div className="mid">
                <Container fluid>
                    <Row className="justify-content-md-center">
                        <Col xs="12" lg="8">
                        <Card className="shadow" style={{marginBottom:'1rem',padding:'1rem',border:'none'}}>
                            <Map
                                width={ width > 980 ? 960 : width - 80 }
                                zoom={2}
                                center={[20.505,-0.09]}
                                minZoom={2}
                                >
                                <TileLayer url="https://{s}.tile.osm.org/{z}/{x}/{y}.png" />

                                {
                                incidents.map((city,i) => {
                                    return (
                                    <CircleMarker 
                                        key={i} 
                                        center={[city.lat, city.long]} 
                                        readius={20 * Math.log(city.deaths / total)}
                                        fillOpacity={0.5}
                                        fillColor={"red"}
                                        stroke={false}
                                    />
                                    )
                                })
                                }
                            </Map>
                        </Card>     
                        </Col>
                    </Row>
                </Container>
                <Container fluid>
                    <Row className="justify-content-md-center">
                        <Col xs lg="8">
                        <Card className="shadow-sm" style={{marginBottom:'1rem',padding:'1rem',border:'none'}}>
                            <Row className="justify-content-md-center">
                                <Col>
                                    <Alert style={{backgroundColor:colors.confirmed}}>
                                    <h4>
                                    <NumberFormat 
                                        value={confirmed} 
                                        displayType={'text'} 
                                        thousandSeparator={true} 
                                    />  
                                    </h4>  
                                    <p><b>Accomulative</b><i> as of today</i></p> 
                                    </Alert>
                                </Col>
                                <Col>
                                    <Alert style={{backgroundColor:colors.recovered}}>
                                    <h4>
                                    <NumberFormat 
                                        value={recovered} 
                                        displayType={'text'} 
                                        thousandSeparator={true} 
                                    />
                                    </h4>
                                    <p><b>{Math.round((recovered / confirmed) * 100)}</b><i>% Recovery</i></p>  
                                    </Alert>
                                </Col>
                                <Col>
                                    <Alert style={{backgroundColor:colors.deaths,color:'#FFFFFF'}}>
                                    <h4>
                                    <NumberFormat 
                                        value={deaths} 
                                        displayType={'text'} 
                                        thousandSeparator={true} 
                                    />
                                    </h4>
                                    <p><b>{Math.round((deaths / confirmed) * 100)}</b><i>% Deaths</i></p>   
                                    </Alert>
                                </Col>
                            </Row>
                        </Card>     
                        </Col>
                    </Row>
                </Container>

                <Container fluid>
                    <Row className="justify-content-md-center">
                        <Col xs lg="8">
                        <Card className="shadow-sm" style={{marginBottom:'1rem',padding:'1rem',border:'none'}}>
                        <BarChart width={600} height={300} data={incidentsSorted}
                                margin={{top: 5, right: 30, left: 20, bottom: 5}}>
                        <CartesianGrid strokeDasharray="3 3"/>
                        <XAxis dataKey="countryRegion"/>
                        <YAxis/>
                        <Tooltip/>
                        <Legend />
                        <Bar dataKey="confirmed" fill="#8884d8" />
                        <Bar dataKey="recovered" fill="#8884d8" />
                        <Bar dataKey="deaths" fill="#8884d8" />
                        </BarChart>
                        </Card>     
                        </Col>
                    </Row>
                </Container>
            </div>
        )
    }
}
export default LandingPage;
