import React, { Component } from 'react';
import Axios from 'axios';
import { Map, CircleMarker,TileLayer} from 'react-leaflet';
import {Container,Row,Col, Card} from 'react-bootstrap';

class LandingPage extends Component{
    state={
        total:0,
        incidents:[],
        width:
        window.innerWidth ||
        document.documentElement.clientWidth ||
        document.body.clientWidth
      }

      componentDidMount(){
        this.getData();
        window.addEventListener('resize',this.updateDimensions);
      }
      updateDimensions = ()=>{
        const width = 
        window.innerWidth ||
        document.documentElement.clientWidth ||
        document.body.clientWidth;

        this.setState({width});
    }
      async getData(){
      const res=await Axios.get("https://covid19.mathdro.id/api/deaths");
      const totalDeaths=await Axios.get("https://covid19.mathdro.id/api");
      this.setState({
        incidents:res.data.filter(row=> row.lat !== null),
        total:totalDeaths.data.deaths.value
      })
      console.log(this.state.incidents);
      }
    render(){
        const {
            total,
            incidents,
            width
        } = this.state;
        return(
            <div className="mid">
                <Container fluid>
                    <Row className="justify-content-md-center">
                        <Col xs="12" lg="8">
                        <Card className="shadow-sm" style={{marginBottom:'1rem',padding:'1rem',border:'none'}}>
                            <Map 
                                width={ width > 980 ? 960 : width - 80 }
                                zoom={1}
                                center={[-0.09,51.505]}
                                >
                                <TileLayer url="http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

                                {
                                incidents.map((city,i) => {
                                    return (
                                    <CircleMarker 
                                        key={i} 
                                        center={[city.lat, city.long]} 
                                        readius={20 * Math.log(city.deaths / total)}
                                        fillOpacity={0.5}
                                        fillColor={"red"}
                                        stroke={false}
                                    />
                                    )
                                })
                                }
                            </Map>
                        </Card>     
                        </Col>
                    </Row>
                </Container>
            </div>
        )
    }
}
export default LandingPage;
