import React, { Component } from 'react';
import Axios from 'axios';
import { LineChart, Line, XAxis,YAxis, CartesianGrid,Tooltip,Legend } from 'recharts';
import './App.css';

class App extends Component {
  state={
    countries:[]
  }
  componentDidMount(){
    this.getData();
  }
  async getData(){
    const res=await Axios.get("https://pomber.github.io/covid19/timeseries.json");

    this.setState({
      countries:res.data
    })
    console.log(this.state.total);
  }
  render(){
    return (
      <div className="App">
        <h1>Hi</h1>
        {
          this.state.countries.China.map((list,i)=>
        <li key={i}>{list.date} deaths: {list.deaths}</li>
          )
        }

        <div className="chart">
          <p>I am here</p>
          <LineChart width={1200} height={600} data={this.state.countries}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="confirmed" stroke="#1B262C" />
          <Line type="monotone" dataKey="recovered" stroke="#323Edd" />
          <Line type="monotone" dataKey="deaths" stroke="#C81912" />
          </LineChart>
        </div>
      </div>

      )
  }
}

export default App;
