import React,{ Component } from 'react';
import Axios from 'axios';
import {Container,Row,Col,Table,Form, Card, CardDeck} from 'react-bootstrap';

const trumpNewsEndpoint="https://newsapi.org/v2/top-headlines?q=trump&apiKey=1eef6d1799164641972598884245ee39";
class News extends Component{
    state={
        trumpNews:[]
    }

    componentDidMount(){
        this.getNews();
    }
    async getNews(){
        const trumpNewsRes=await Axios.get(trumpNewsEndpoint);
        console.log(trumpNewsRes.data.articles);
        this.setState({
            trumpNews:trumpNewsRes.data.articles
        })
    }
    render(){
        return(
            <div className="mid">
                <Container fluid>
                    <Row className="justify-content-md-center">
                        <Col xs="12" lg="8">
                            <CardDeck>
                                <Card className="shadow-sm" style={{marginBottom:'1rem',padding:'1rem',border:'none'}}>

                                </Card> 
                            </CardDeck>    
                        </Col>
                    </Row>
                </Container>
            </div>
        )
    }
}
export default News;
