import React, { Component } from 'react';
import Axios from 'axios';
import {Container,Row,Col,Table,Form, Card, CardDeck} from 'react-bootstrap';
import NumberFormat from 'react-number-format';
import { AreaChart, XAxis,YAxis, CartesianGrid,Tooltip,Legend, Area} from 'recharts';
import Pagination from './pagination';

const colors = {
    confirmed: 'hsla(27, 100%, 51%, 1)',
    recovered: 'hsla(91, 100%, 51%, 1)',
    deaths: 'hsla(0, 100%, 51%, 1)',
  };

class DataChartOnly extends Component{
    constructor (props){
        super(props);
        this.getCountry=this.getCountry.bind(this);
      }

      state={
        currentData:[],
        currentPage:1,
        dataPerPage:7,
        selectedCountry:"China",
        temp:[],
        countries:[],
        tableCountries:[],
        width:
        window.innerWidth ||
        document.documentElement.clientWidth ||
        document.body.clientWidth
      }
      componentDidMount(){
        this.getData();
        window.addEventListener('resize',this.updateDimensions);
