import React, { Component } from 'react';
import Axios from 'axios';
import './App.css';

class App extends Component {
  state={
    total:[
      {
        date:"",
        confirmed:0,
        recovered:0,
        deaths:0
      },
      {
        date:"",
        confirmed:0,
        recovered:0,
        deaths:0
      }
    ],
    countries:[]
  }
  componentDidMount(){
    this.getData();
  }
  async getData(){
    const res=await Axios.get("https://pomber.github.io/covid19/timeseries.json");

    this.setState({
      countries:res.data.China
    })
    console.log(this.state.total);
  }
  render(){
    return (
      <div className="App">
        <h1>Hi</h1>
        {
          this.state.countries.map((list,i)=>
        <li key={i}>{list.date} deaths: {list.deaths}</li>
          )
        }
      </div>

      )
  }
}

export default App;
