import React, { Component } from 'react';
import Axios from 'axios';
import {Container,Row,Col,Table,Form} from 'react-bootstrap';
import NumberFormat from 'react-number-format';
import { LineChart, Line, XAxis,YAxis, CartesianGrid,Tooltip,Legend} from 'recharts';

class DataChart extends Component{
    constructor (props){
        super(props);
        this.getCountry=this.getCountry.bind(this);
      }
      state={
        temp:[],
        countries:[]
      }
      componentDidMount(){
        this.getData();
      }
      async getData(){
        const res=await Axios.get("https://pomber.github.io/covid19/timeseries.json");

        this.setState({
          countries:res.data.China,
          temp:Object.keys(res.data)
        })
        console.log(this.state.temp);
      }
      async getCountry(event){
        event.persist();
        const countryRes=await Axios.get("https://pomber.github.io/covid19/timeseries.json");
        const searchCountry=event.target.value;
        console.log(countryRes.data[searchCountry]);

        this.setState({
          countries:countryRes.data[searchCountry]
        })
      }
render(){
    return(
        <div className="dataChart">
            <Container>
                <h4>Visual Chart of COVID Statistics</h4>
                <Row className="justify-content-md-center">
                    <Col lg="12" md="auto">
                    <LineChart width={500} height={500} data={this.state.countries}>
                    <CartesianGrid strokeDasharray="1 1" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line type="monotone" dataKey="confirmed" dot={false} points={1} stroke="#1B262C" strokeWidth={2} />
                    <Line type="monotone" dataKey="recovered" dot={false} points={1} stroke="#323Edd" strokeWidth={2} />
                    <Line type="monotone" dataKey="deaths" dot={false} points={1} stroke="#C81912" strokeWidth={2} />
                    </LineChart>
                    </Col>
                </Row>
            </Container>
            <Container>
                <Row className="justify-content-md-center">
                    <Col lg="12" md="auto">
                        <Form>
                        <Form.Group controlId="exampleForm.SelectCustomSizeLg">
                            <Form.Label>Select Country</Form.Label>
                            <Form.Control as="select" size="lg" onChange={this.getCountry} custom>
                                {
                                this.state.temp.map((items,i)=>
                                <option key={i}>{items}</option>  
                                )
                                }
                            </Form.Control>
                        </Form.Group>
                        </Form>      
                    </Col>
                </Row>
            </Container>
            <Container>
                <Row className="justify-content-md-center">
                    <Col lg="12" md="auto">
                    <Table responsive size="sm" hover>
                    <thead>
                        <tr>
                        <th>Date</th>
                        <th>Confirmed</th>
                        <th>Recovered</th>
                        <th>Deaths</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                        this.state.countries.map((list,index)=>
                        <tr key={index}>
                        <td>{list.date}</td>
                        <td><NumberFormat value={list.confirmed} displayType={'text'} thousandSeparator={true} /></td>
                        <td><NumberFormat value={list.recovered} displayType={'text'} thousandSeparator={true} /></td>
                        <td><NumberFormat value={list.deaths} displayType={'text'} thousandSeparator={true} /></td>
                        </tr>
                        )
                        }
                    </tbody>
                    </Table>
                    </Col>
                </Row>
            </Container>
        </div>
    )
}
}
export default DataChart;
