import React, { Component } from 'react';
import Axios from 'axios';
import { LineChart, Line } from 'recharts';
import './App.css';

class App extends Component {
  state={
    chartdata:[
      {
        name:'A',
        uv:400,
        pv:2400,
        amt:2400
      },
      {
        name:'B',
        uv:500,
        pv:2400,
        amt:1800
      },
      {
        name:'B',
        uv:200,
        pv:2400,
        amt:3000
      }
    ],
    countries:[]
  }
  componentDidMount(){
    this.getData();
  }
  async getData(){
    const res=await Axios.get("https://pomber.github.io/covid19/timeseries.json");

    this.setState({
      countries:res.data.China
    })
    console.log(this.state.total);
  }
  render(){
    return (
      <div className="App">
        <h1>Hi</h1>
        {
          this.state.countries.map((list,i)=>
        <li key={i}>{list.date} deaths: {list.deaths}</li>
          )
        }
        <div className="chart">
          <p>I am here</p>
          <LineChart width={400} height={400} data={this.state.chartdata}>
            <Line type="monotone" datakey="uv" stroke="#8884d8"></Line>
          </LineChart>
        </div>
      </div>

      )
  }
}

export default App;
