import React, { Component } from 'react';
import Axios from 'axios';
import {Container,Row,Col,Table,Form, Card} from 'react-bootstrap';
import NumberFormat from 'react-number-format';
import ReactGA from 'react-ga';
import Pagination from './pagination';
import Loader from 'react-loader-spinner';

const colors = {
    confirmed: '#FFD31D',
    recovered: '#21BF72',
    deaths: '#DD2C00',
  };

  const showChartPeriod=[
    {
        period:7,
        periodTitle:"Sow Last 7 Days Data"
    },
    {
        period:14,
        periodTitle:"Sow Last 14 Days Data"
    },
    {
        period:30,
        periodTitle:"Sow Last 30 Days Data"
    }
]

class DataChart extends Component{
    constructor (props){
        super(props);
        this.getData=this.getData.bind(this);
        this.getCountry=this.getCountry.bind(this);
        this.updateChartPeriod=this.updateChartPeriod.bind(this);
        this.paginate=this.paginate.bind(this);
      }

      state={
        currentData:[],
        currentPage:1,
        dataPerPage:14,
        selectedCountry:"China",
        population:0,
        temp:[],
        countries:[],
        tableCountries:[],
        width:
        window.innerWidth ||
        document.documentElement.clientWidth ||
        document.body.clientWidth
      }
      componentDidMount(){

        ReactGA.initialize('UA-163115935-1');
        ReactGA.pageview('/Daily-Data');

        this.getCountryByIP();
        window.addEventListener('resize',this.updateDimensions);
      }
      updateDimensions = ()=>{
          const width = 
          window.innerWidth ||
          document.documentElement.clientWidth ||
          document.body.clientWidth;

          this.setState({width});
      }
      async getCountryByIP(){
        const res=await Axios.get("https://ipapi.co/json");
        this.setState({
            selectedCountry:res.data.country_name ==="United States" ? "US" : res.data.country_name,
              population:res.data.country_population
        })
        this.getData();
    }
      async getData(){
        const res=await Axios.get("https://pomber.github.io/covid19/timeseries.json");
        const selectedCountry=this.state.selectedCountry;
        const byConfirmed=res.data[selectedCountry].slice(0);
        byConfirmed.sort(function(a,b){
            return b.confirmed - a.confirmed;
        });

        this.setState({
          countries:res.data[selectedCountry],
          tableCountries:byConfirmed,
          temp:Object.keys(res.data)
        })

        const currentData = this.state.dataPerPage == 0 ? 
        this.state.tableCountries :
        ()=>
        {
            const indexOfLastData=this.state.currentPage * this.state.dataPerPage;
            const indexOfFirstData=indexOfLastData - this.state.dataPerPage;
            return(this.state.tableCountries.slice(indexOfFirstData,indexOfLastData));
        }


        this.setState({
            currentData
        })


      }
      async getCountry(event){
        event.persist();
        const countryRes=await Axios.get("https://pomber.github.io/covid19/timeseries.json");
        const searchCountry=event.target.value;

        const byConfirmed=countryRes.data[searchCountry].slice(0);
        byConfirmed.sort(function(a,b){
            return b.confirmed - a.confirmed;
        });

        this.setState({
            selectedCountry:searchCountry,
            tableCountries:byConfirmed
        })

        const dataPerPage = this.state.dataPerPage == 0 ? this.state.tableCountries.length-1 : this.state.dataPerPage;
        const indexOfLastData=this.state.currentPage * dataPerPage;
        const indexOfFirstData=indexOfLastData - dataPerPage;

        this.setState({
            currentData:this.state.tableCountries.slice(indexOfFirstData,indexOfLastData)
        })
      }

      updateChartPeriod(event){
        event.persist();

        this.setState({
            dataPerPage:event.target.value,
        })
        this.getData();
        //console.log(event.target.value);
      }

      async updatePageNumber(){
        const countryRes=await Axios.get("https://pomber.github.io/covid19/timeseries.json");
        const searchCountry=this.state.selectedCountry;

        const byConfirmed=countryRes.data[searchCountry].slice(0);
        byConfirmed.sort(function(a,b){
            return b.confirmed - a.confirmed;
        });

        this.setState({
            selectedCountry:searchCountry,
            tableCountries:byConfirmed
        })



        const indexOfLastData=this.state.currentPage * this.state.dataPerPage;
        const indexOfFirstData=indexOfLastData - this.state.dataPerPage;
        this.setState({
            currentData:this.state.tableCountries.slice(indexOfFirstData,indexOfLastData)
        })

      }

    //paginate = pageNumber=> this.setState({currentPage:pageNumber});
        paginate(pageNumber){
        this.setState({currentPage:pageNumber});
        this.updatePageNumber();
    }

render(){
    const {
        currentData,
        dataPerPage,
        selectedCountry,
        population,
        temp,
        tableCountries,
        width
    } = this.state;

    if(!currentData) return (
        <Container fluid>
            <Row className="justify-content-md-center">
                <Col xs="12" lg="8">
                <Card 
                    className="shadow" 
                    style={{marginBottom:'1rem',paddingTop:'15rem',border:'none',minHeight:"700px"}}
                >
                    <Loader 
                        type="ThreeDots"
                        color="#DD2C00"
                        height={100}
                        width={100}
                        timeout={900000000}
                    />
                </Card>     
                </Col>
            </Row>
        </Container>
    );

    return(
        <div className="mid">
            <Container fluid>
                <Row className="justify-content-md-center">
                    <Col xs="12" lg="8">
                    <Card className="shadow-sm" style={{marginBottom:'1rem',padding:'1rem',border:'none'}}>
                        <Form.Control as="select" size="sm" onChange={this.getCountry} custom>
                                <option>{selectedCountry}</option> 
                                {
                                temp.map((items,i)=>
                                <option key={i}>{items}</option>  
                                )
                                }
                            </Form.Control>
                    </Card>     
                    </Col>
                </Row>

                <Row className="justify-content-md-center">
                    <Col xs="12" lg="8">
                    <Card className="shadow-sm" style={{marginBottom:'1rem',padding:'1rem',border:'none'}}>
                        <Form.Control as="select" size="sm" onChange={this.updateChartPeriod} custom>

                                {
                                showChartPeriod.map((items,index)=>
                                <option key={index} value={items.period}>{items.periodTitle}</option>  
                                )
                                }
                                <option value={0}>Show All Data</option> 
                            </Form.Control>
                    </Card>     
                    </Col>
                </Row>
            </Container>

            <Container fluid>
                <Row className="justify-content-md-center">
                    <Col xs lg="8">
                        <Card className="shadow-sm" style={{marginTop:'1rem',padding:'1rem',border:'none'}}>
                            <Table responsive size="sm" striped={true} bordered={true} hover>
                                <thead>
                                    <tr>
                                    <th>Date</th>
                                    <th>Confirmed</th>
                                    <th>Recovered</th>
                                    <th>Deaths</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                    currentData.map((list,index)=>
                                    <tr key={index}>
                                    <td>{list.date}</td>
                                    <td>
                                        <NumberFormat 
                                            value={list.confirmed} 
                                            displayType={'text'} 
                                            thousandSeparator={true} 
                                        />

                                    </td>
                                    <td>
                                        <NumberFormat value={list.recovered} displayType={'text'} thousandSeparator={true} />
                                    </td>
                                    <td style={{fontWeight:"bold"}}>
                                        <NumberFormat value={list.deaths} displayType={'text'} thousandSeparator={true} />
                                    </td>
                                    </tr>
                                    )
                                    }
                                </tbody>
                                </Table>
                                <Pagination  
                                    dataPerPage={dataPerPage} 
                                    totalData={tableCountries.length} 
                                    paginate={this.paginate}
                                />
                        </Card>

                    </Col>
                </Row>
            </Container>

        </div>
    )
}
}
export default DataChart;
