import React,{ Component } from 'react';
import {Row,Col, Card, Alert} from 'react-bootstrap';

const turkey={
    title:"Dünyanın en düşük spread forex brokeri | Canlı Hesabı Şimdi Aç"
}
class LandingPageAdvert extends Component{
    render(){
        return(
            <Row className="justify-content-md-center">
                <Col xs="12" lg="8">
                    <Card className="shadow=sm" style={{marginBottom:'1rem',padding:'1rem',border:'none'}}>
                        <Card.Img 
                            variant="top"  
                            src={require('../assets/img/Pepperstone-FOREX-BROKER.jpg')}
                            style={{
                                width:"100%",
                                height:"5rem"
                            }} 
                        />
                        <Card.Body>
                            <Card.Title style={{fontSize:'medium',textAlign:'left'}}>{turkey.title}</Card.Title>
                            <Card.Text style={{overflow:'hidden',textOverflow:'ellipsis',maxHeight:'4rem',whiteSpace:'pre-wrap'}}>
                                Join Now
                            </Card.Text>
                        </Card.Body>
                    </Card>     
                </Col>
            </Row>
        )
    }
}
export default LandingPageAdvert;
